export const styles = theme => ({
  searchRoot: {
    marginBottom: theme.spacing.unit * 3,
    minHeight: theme.spacing.unit * 6,
    display: 'flex',
  },
  root: {
    width: '100%',
    overflowX: 'auto',
  },
});
